CREATE TABLE SCHOOL (  SCHOOL_ID INT GENERATED ALWAYS AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,  SCHOOL_NUMBER INT,  SCHOOL_NAME VARCHAR(128));
CREATE TABLE BATCH (  BATCH_ID INT GENERATED ALWAYS AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,  BATCH_NUMBER VARCHAR(45),  BATCH_SCHOOL_ID INT NOT NULL,  CONSTRAINT fk_BATCH_SCHOOL1    FOREIGN KEY (BATCH_SCHOOL_ID)    REFERENCES SCHOOL (SCHOOL_ID));
CREATE TABLE STUDENT (  STUD_ID INT GENERATED ALWAYS AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,  STUD_FNAME VARCHAR(128),  STUD_LNAME VARCHAR(128),  STUD_PNAME VARCHAR(128),  STUD_NUM_ZACH VARCHAR(45),  STUD_BATCH_ID INT NOT NULL,  CONSTRAINT fk_STUDENT_BATCH    FOREIGN KEY (STUD_BATCH_ID)    REFERENCES BATCH (BATCH_ID));